USE test;
DROP TABLE IF EXISTS `articles`;
CREATE TABLE IF NOT EXISTS `articles` (
    id  BIGINT(20) UNSIGNED NOT NULL AUTO_INCREMENT,
    author BIGINT(20) UNSIGNED NOT NULL,
    title VARCHAR(500) NOT NULL,
    intro_text TEXT NOT NULL,
    full_text LONGTEXT NOT NULL,
    created_at DATETIME NOT NULL,
    modifyed_at TIMESTAMP NOT NULL DEFAULT '0000-00-00 00:00:00' ON UPDATE CURRENT_TIMESTAMP,
    PRIMARY KEY (`id`)
)  ENGINE=InnoDB DEFAULT CHARSET=utf8;

UPDATE articles SET author = 1, title = 'Густав Малер', intro_text = 'Густав Малер (нем. Gustav Mahler; 7 июля 1860, Калиште[cs], Богемия — 18 мая 1911, Вена) — австрийский композитор, оперный и симфонический дирижёр.', full_text = 'При жизни Густав Малер был знаменит прежде всего как один из крупнейших дирижёров своего времени, представитель так называемой «послевагнеровской пятёрки». Хотя искусству управления оркестром Малер никогда не учился сам и никогда не обучал других, влияние, оказанное им на младших коллег, позволяет музыковедам говорить о «малеровской школе», причисляя к ней и таких выдающихся дирижёров, как Виллем Менгельберг, Бруно Вальтер и Отто Клемперер.

Малер-композитор имел при жизни лишь относительно узкий круг преданных поклонников, и только спустя полвека после смерти он получил настоящее признание — как один из крупнейших симфонистов XX века. Творчество Малера, ставшее своего рода мостом между поздним австро-германским романтизмом XIX века и модернизмом начала XX, оказало влияние на многих композиторов, в том числе и таких разных, как представители Новой венской школы, с одной стороны, Дмитрий Шостакович и Бенджамин Бриттен — с другой.

Наследие Малера-композитора, относительно небольшое и почти полностью состоящее из песен и симфоний, за последние полвека прочно вошло в концертный репертуар, и вот уже несколько десятилетий он является одним из самых исполняемых композиторов.', created_at = '2016-01-28 20:00:00', modifyed_at = '2016-01-28 00:28:25' WHERE id = 1;
UPDATE articles SET author = 1, title = 'Алишера Навои (станция метро)', intro_text = '«Алишера Навои» (узб. Alisher Navoiy) — станция Узбекистанской линии Ташкентского метрополитена. Расположена между станциями «Гафура Гуляма» и «Узбекистанская».', full_text = 'Названа по проспекту Навои, под которым она расположена. В свою очередь проспект назван в честь поэта Алишера Навои. Открыта 8 декабря 1984 года в составе участка «Алишера Навои» — «Ташкент» (первая очередь строительства линии).', created_at = '2016-01-28 21:00:00', modifyed_at = '2016-01-28 00:28:39' WHERE id = 2;
UPDATE articles SET author = 1, title = 'Дети в Византии', intro_text = 'Дети в Византии выделялись в отдельную категорию жителей империи. С точки зрения законодательства полная правоспособность наступала в возрасте 25 лет, однако в других контекстах предельными были другие значения. Источниками знаний о византийских детях являются в основном правовые и агиографические тексты.', full_text = 'Важным аспектом жизни ребёнка в Византии являлось получение им образования, обычно начинавшегося в возрасте шести или семи лет. Распространённым явлением в Византии являлись браки среди детей, часто совершавшиеся в возрасте около 10 лет. В этом же возрасте дети могли вступать в монастыри. Количество детей в семьях в среднем было невелико, однако за счёт невысокой продолжительности жизни их доля в обществе была значительна. Ожидания от детей были те же, что и в современном обществе, — продолжение рода, наследование семейной собственности, забота о своих родителях, их погребении и посмертной памяти.

В Византии государство уделяло значительное внимание положению детей, оставшихся без попечения родителей. Законодательство устанавливало ответственность за оставление детей, поощряло взятие в семью приёмных детей. Существовала система государственных приютов. В отличие от Древнего Рима, где отец семейства был властен над жизнью и смертью ребёнка, в Византии детоубийство было запрещено.

В настоящее время изучение детства в Византии идёт по многим направлениям. Исследования, проводимые на основе анализа сохранившихся литературных источников, позволили изучить вопросы социальной идентичности, правового статуса, образования и материального положения детей, влияния на них культурного и религиозного окружения. Начиная с конца XX века появились многочисленные археологические данные, важные для более детального понимания жизни и здоровья византийских детей.', created_at = '2016-01-28 22:00:00', modifyed_at = '2016-01-28 00:29:04' WHERE id = 3;
UPDATE articles SET author = 2, title = 'Белозо́р боло́тный, или Золотни́чка боло́тная', intro_text = 'Белозо́р боло́тный, или Золотни́чка боло́тная[2] (лат. Parnássia palústris) — многолетнее травянистое наземное растение, обычное почти на всей территории Европы, включая Россию; типовой вид рода Белозор семейства Белозоровые.', full_text = 'Строение цветка белозора болотного весьма необычно. Между полосатыми лепестками и тычинками находится круг стаминодиев (так называются тычинки, превращающиеся в бесплодные образования). На краях этих образований сидят «булавочки» с тонкими ножками и головками на концах их. Эти головки очень похожи на капельки нектара или мёда, и насекомые, введённые в заблуждение, прилетают на цветок и содействуют опылению[3].

Растение изредка используется как садовое декоративное растение. В прошлом сушёную траву белозора болотного широко применяли в народной медицине.', created_at = '2016-01-28 23:00:00', modifyed_at = '2016-01-28 00:29:44' WHERE id = 4;
